sqlMap.select:
MERGE Basis.BIN_ProductSpecialOfferSolu AS TGT USING (select #organizationInfoId# as orgInfoId, #brandInfoId# as brandInfoId, #CounterCode# as solutionCode ) AS SRC ON ( TGT.BIN_OrganizationInfoID = SRC.orgInfoId AND TGT.BIN_BrandInfoID = SRC.brandInfoId AND TGT.SpecialOfferSoluCode = SRC.solutionCode ) WHEN MATCHED THEN UPDATE SET SpecialOfferSoluName = #specialOfferSoluName#, ValidFlag = '1',
WHEN NOT MATCHED THEN INSERT( BIN_OrganizationInfoID, BIN_BrandInfoID, SpecialOfferSoluCode, SpecialOfferSoluName, SpecialOfferSoluStartDate, SpecialOfferSoluEndDate, Comments,
)VALUES( #organizationInfoId#, #brandInfoId#, #CounterCode#, #specialOfferSoluName#, #RunStartTime#, #SpecialOfferSoluEndDate#, #Comments#,
) OUTPUT $$action AS actionResult ,inserted.BIN_ProductSpecialOfferSoluID AS BIN_ProductSpecialOfferSoluID ,inserted.SpecialOfferSoluCode AS SpecialOfferSoluCode;

MERGE Basis.BIN_PrtSpecialOfferSoluDepartRelation AS TGT USING (select #organizationInfoId# as orgInfoId, #brandInfoId# as brandInfoId, #CounterCode# as cntCode, #BIN_SolutionId# as solutionId ) AS SRC ON ( TGT.BIN_OrganizationInfoID = SRC.orgInfoId AND TGT.BIN_BrandInfoID = SRC.brandInfoId AND TGT.DepartCode = SRC.cntCode AND TGT.BIN_SpecialOfferSolutionId = SRC.solutionId ) WHEN MATCHED THEN UPDATE SET Version = #pdTVersion# + 1, ValidFlag = '1',
WHEN NOT MATCHED THEN INSERT( BIN_OrganizationInfoID, BIN_BrandInfoID, BIN_SpecialOfferSolutionId, SpecialOfferSoluCode, DepartCode, Version,
)VALUES( #organizationInfoId#, #brandInfoId#, #BIN_SolutionId#, #SpecialOfferSoluCode#, #CounterCode#, #pdTVersion# + 1,
) OUTPUT $$action AS actionResult,inserted.DepartCode AS DepartCode,inserted.BIN_SpecialOfferSolutionId AS BIN_SolutionId;

sqlMap.insert:
INSERT INTO Basis.BIN_ProductPrice( BIN_ProductID, SalePrice, MemPrice, PlatinumPrice, StartDate, EndDate, Type,
)VALUES ( #productId#, Convert(decimal(16,4),#SalePrice#), Convert(decimal(16,4),#MemPrice#), Convert(decimal(16,4),#MemPrice2#), #startDate#, #endDate#, '2',
)

insert.selectKey:
INSERT INTO Basis.BIN_ProductVendor( BIN_ProductID, BarCode,
)VALUES( #productId#, #BarCode#,
) SELECT SCOPE_IDENTITY() AS value

INSERT Basis.BIN_PrtCatProperty( BIN_OrganizationInfoID, BIN_BrandInfoID, PropertyNameChinese, PropertyNameForeign, ViewSeq, TeminalFlag, Property,
)VALUES( #organizationInfoId#, #brandInfoId#, #propNameCN#, #propNameEN#, IDENT_CURRENT('Basis.BIN_PrtCatProperty'), #teminalFlag#, #property#,
) select SCOPE_IDENTITY() as value

INSERT Basis.BIN_PrtCatPropValue( BIN_PrtCatPropertyID, PropValue, PropValueCherry, PropValueChinese, PropValueForeign,
)VALUES( #prtCatPropId#, #propValue#, #propValueCherry#, #propValueCN#, #propValueEN#,
) select SCOPE_IDENTITY() as value
