sqlMap.select:
SELECT T.id, T.name, T.type, T.path, T.level FROM (SELECT convert(varchar(20),A.BIN_OrganizationID) AS id, A.DepartName AS name, A.Type AS type, A.Path AS path, A.Level AS level FROM Basis.BIN_Organization A WHERE A.Type <> '4' AND A.BIN_BrandInfoID = #brandInfoId#
UNION SELECT convert(varchar(20),A.BIN_OrganizationID) AS id, A.DepartName AS name, A.Type AS type, A.Path AS path, A.Level AS level FROM Basis.BIN_Organization A JOIN (
) A99 ON A.BIN_OrganizationID=A99.BIN_OrganizationID WHERE A.Type = '4' AND A.BIN_BrandInfoID = #brandInfoId# AND A.ValidFlag = '1' ) T ORDER BY T.path
