insert.selectKey:
INSERT Basis.BIN_PrtCatProperty( BIN_OrganizationInfoID, BIN_BrandInfoID, PropertyNameChinese, PropertyNameForeign, ViewSeq, TeminalFlag,
)VALUES( #organizationInfoId#, #brandInfoId#, #propNameCN#, #propNameEN#, IDENT_CURRENT('Basis.BIN_PrtCatProperty'), #teminalFlag#,
) select SCOPE_IDENTITY() as value

INSERT Basis.BIN_PrtCatPropValue( BIN_PrtCatPropertyID, PropValue, PropValueCherry, PropValueChinese, PropValueForeign, BindBigClass,
)VALUES( #propId#, #propValue#, #propValueCherry#, #propValueCN#, #propValueEN#, #bindBigClass#,
) select SCOPE_IDENTITY() as value
