sqlMap.select:
MERGE Campaign.BIN_Campaign AS TGT USING ( SELECT A.BIN_OrganizationInfoID, A.BIN_BrandInfoID, A.RuleCode, A.RuleName, A.StartTime, A.EndTime, B.DescriptionDtl, B.ActivitySetBy, B.ValidFlag FROM Promotion.BIN_PromotionRule A JOIN Promotion.BIN_PromotionActivity B ON(A.RuleCode = B.ActivityCode AND A.BIN_BrandInfoID = B.BIN_BrandInfoID) where A.RuleCode = #ruleCode# AND A.BIN_BrandInfoID = #brandInfoId# ) as SRC ON(TGT.BIN_BrandInfoID = SRC.BIN_BrandInfoID AND TGT.CampaignCode = 'AS'+SRC.RuleCode)
WHEN MATCHED THEN UPDATE SET TGT.CampaignName = SRC.RuleName, TGT.CampaignFromDate = SRC.StartTime, TGT.CampaignToDate = SRC.EndTime, TGT.ObtainFromDate = SRC.StartTime, TGT.ObtainToDate = SRC.EndTime, TGT.NeedBuyFlag = #needBuyFlag#, TGT.DescriptionDtl = SRC.DescriptionDtl, TGT.ValidFlag = SRC.ValidFlag
WHEN NOT MATCHED THEN INSERT ( BIN_OrganizationInfoID, BIN_BrandInfoID, CampaignCode, CampaignType, CampaignName, CampaignFromDate, CampaignToDate, ObtainFromDate, ObtainToDate, DescriptionDtl, CampaignSetBy, CampaignLeader, NeedBuyFlag, CampaignTypeFlag, GotCounter, State, SaveStatus, ValidFlag )values( SRC.BIN_OrganizationInfoID, SRC.BIN_BrandInfoID, 'AS'+SRC.RuleCode, 'CXHD', SRC.RuleName, SRC.StartTime, SRC.EndTime, SRC.StartTime, SRC.EndTime, SRC.DescriptionDtl, SRC.ActivitySetBy, SRC.ActivitySetBy, #needBuyFlag#, '1', '1', 0, '1', SRC.ValidFlag ) OUTPUT inserted.BIN_CampaignID;

MERGE Campaign.BIN_CampaignRule AS TGT USING ( SELECT #campId# AS BIN_CampaignID, B.ActivityCode, B.ActivityName, B.DescriptionDtl, B.ValidFlag, A.SubCampaignValid FROM Promotion.BIN_PromotionRule A JOIN Promotion.BIN_PromotionActivity B ON(A.RuleCode = B.ActivityCode AND A.BIN_BrandInfoID = B.BIN_BrandInfoID) where A.RuleCode = #ruleCode# AND A.BIN_BrandInfoID = #brandInfoId#
) AS SRC ON(TGT.BIN_CampaignID = SRC.BIN_CampaignID)
WHEN MATCHED THEN UPDATE SET TGT.SubCampaignCode = SRC.ActivityCode, TGT.SubCampaignName = SRC.ActivityName, TGT.Description = SRC.DescriptionDtl, TGT.SubCampaignValid = SRC.SubCampaignValid WHEN NOT MATCHED THEN INSERT ( SubCampaignCode, BIN_CampaignID, SubCampaignName, Description, State, SubCampaignType, SubCampaignValid, ValidFlag )values( SRC.ActivityCode, #campId#, SRC.ActivityName, SRC.DescriptionDtl, 0, 'ZNCX', SRC.SubCampaignValid, SRC.ValidFlag ) OUTPUT inserted.BIN_CampaignRuleID;
