sqlMap.select:
select T.*, isnull(T2.ExPoint,0) AS ExPoint from(
UNION
) T Left JOIN ( SELECT B.SubCampaignCode, E.ExPoint From Campaign.BIN_CampaignRule B JOIN Campaign.BIN_CampaignRuleResult C on(B.BIN_CampaignRuleID = C.BIN_CampaignRuleID AND C.SaleType = 'P' AND C.ValidFlag = '1') JOIN Basis.BIN_PromotionProductVendor D on(c.BIN_ProductVendorID = D.BIN_PromotionProductVendorID) JOIN Basis.BIN_PromotionProduct E ON(D.BIN_PromotionProductID = E.BIN_PromotionProductID AND E.PromotionCateCD = 'DHCP') ) T2 on(T.SubCampCode = T2.SubCampaignCode)
